<?phpnamespace RapidApi\InstagramScrapper;use GuzzleHttp\Client;use GuzzleHttp\Exception\RequestException;class InstagramClient{    private $client;    private $apiKey;    public function __construct($apiKey) {        $this->client = new Client([            'base_uri' => 'https://instagram-api.p.rapidapi.com/',            'timeout'  => 5.0,        ]);        $this->apiKey = $apiKey;    }    public function getUserPosts($username) {        try {            $response = $this->client->get("user-posts", [                'headers' => [                    'X-RapidAPI-Host' => 'instagram-api.p.rapidapi.com',                    'X-RapidAPI-Key'  => $this->apiKey                ],                'query' => [                    'username' => $username                ]            ]);            return json_decode($response->getBody(), true);        } catch (RequestException $e) {            return [                'error' => true,                'message' => $e->getMessage()            ];        }    }}